import React from "react";
import  { CheckboxIconSVG,
          FavoriteEmptyIconSVG,
          FavoriteFilledIconSVG
        } from "../../src/index.jsx";


describe("SVG", function() {
  before(() => {
      this.component(
        <div>

      </div> );
  });

  section('CheckboxIconSVG', ()=>{
    before(() => {
        this.component(
          <div>
            <CheckboxIconSVG />
        </div> );
    });
    it("Display/Reset", () => {
      this.component(
        <div>
          <CheckboxIconSVG
            title="This is a title"
            scale=".5"
            fill="orange"
            stroke='none'
            viewBox='0 0 32 32'
          />
        </div> );
    });
  });

  section('FavoriteEmptyIconSVG', ()=>{
    before(() => {
        this.component(
          <div>
            <FavoriteEmptyIconSVG />
        </div> );
    });
    it("Display/Reset", () => {
      this.component(
        <div>
          <FavoriteEmptyIconSVG />
        </div> );
    });
  });

  section('FavoriteFilledIconSVG', ()=>{
    it("Display/Reset", () => {
      this.component(
        <div>
          <FavoriteFilledIconSVG />
        </div> );
    });
  });

});
